# Define the region-to-color mapping
REGION_COLORS = {
    "MLC": "#8B0000",  # DarkRed
    "MLF": "#00008B",  # DarkBlue
    "MLO": "#006400",  # DarkGreen
    "MLP": "#FF8C00",  # DarkOrange
    "MLT": "#4B0082",  # Indigo (dark purple)
    "MRC": "#8B4513",  # SaddleBrown
    "MRF": "#C71585",  # MediumVioletRed (dark pink)
    "MRO": "#008B8B",  # DarkCyan
    "MRP": "#556B2F",  # DarkOliveGreen
    "MRT": "#FFD700",  # Gold (still visible as a dark yellow)
    "MZC": "#800080",  # Purple
    "MZF": "#191970",  # MidnightBlue
    "MZO": "#013220",  # ForestGreen (very dark green)
    "MZP": "#8B0000",  # DarkRed (same as MLC for consistency)
}


# Create a reverse mapping to quickly find the region of a channel
def map_channel_to_color():
    channel_to_region = {}
    for region, channels in GROUP_CHANNELS_BY_REGION.items():
        for channel in channels:
            channel_to_region[channel] = region

    channel_to_color = {}
    for channels, region in channel_to_region.items():
        channel_to_color[channels] = REGION_COLORS[region]
    return channel_to_color

CHANNEL_TO_COLOR = map_channel_to_color()

